import styled from 'styled-components';
import Announcement from '../components/Announcement';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import Newsletter from '../components/Newsletter';
import {Add, Remove} from "@material-ui/icons";
import { mobile } from '../responsive';
import {useLocation} from "react-router-dom";
import { publicRequest } from '../requestMethods';
import { useEffect, useState } from "react";

const Container = styled.div`

`
const Wrapper = styled.div`
  padding: 50px;
  display: flex;
  ${mobile({padding: "10px", flexDirection: "column"})}
`
const ImgContainer = styled.div`
  flex: 1;

`
const Image = styled.img`
  width: 100%;
  object-fit: cover;
  ${mobile({height: "40vh"})}
`
const InfoContainer = styled.div`
  flex: 1;
  padding: 0px 50px;
  ${mobile({padding: "10px"})}
`
const Title = styled.h1`
  font-weight: 200;

`
const Desc = styled.p`
  margin: 20px 0px;
`
const Price = styled.span`
  font-weight: 100;
  font-size: 40px;
`
const FilterContainer = styled.div`
  width: 50%;
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
  ${mobile({width: "100%"})}
`
const Filter = styled.div`
  display: flex; 
  align-items: center;
`
const FilterTitle = styled.span`
  font-size: 20px;
  font-weight: 200;
`
const FilterColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${props=>props.color};
  margin: 0px 10px;
  cursor: pointer;
`
const FilterQuantity = styled.select`
  margin-left: 10px;
  padding: 5px;
`
const FilterQuantityOption = styled.option`
  
`
const AddContainer = styled.div`
  width: 50%;
  display: flex; 
  align-items: center;
  justify-content: space-between;
  ${mobile({width: "100%"})}
`
const AmountContainer = styled.div`
  display: flex; 
  align-items: center;
  font-weight: 700;
`

const BuyWishContainer = styled.div`
  width: 50%;
  display: flex;
  margin: 30px 0px;
  justify-content: space-between;
`

const Amount = styled.span`
  width: 30px;
  height: 30px;
  border-radius: 10px;
  border: 2px solid #005B96;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0px 5px;
`
const Button = styled.button`
  padding: 15px;
  border: 2px solid #005B96;
  background-color: white;
  cursor: pointer;
  font-weight: 500;

  &:hover{
    background-color: #e0ebf2;
  }
`
const WishlistAdd = styled.span`
  width: 30px;
  height: 10px;
  text-decoration: underline;
  margin: 0px 0px;
`


const Product = () => {
  const location = useLocation();
  const id = location.pathname.split("/")[2];

  const [product, setProduct] = useState({});
  const [color, setColor] = useState("");
  const [quantity, setQuantity] = useState(1);

  useEffect(() =>{
    const getProduct = async ()=>{
      try{
        const res = await publicRequest.get("/products/find/" + id)
        setProduct(res.data);
      }catch{}
    };
    getProduct();
  }, [id])

  //function to handle quantity increase and decrease 
  const handleQuantity = (type) =>{
    if(type === "decrease"){
      quantity > 1 && setQuantity(quantity-1);
    }else{
      setQuantity(quantity+1);
    }
  };
  
  const handleClick = ()=>{
    //update cart
    
  }

  return (
    <Container>
        <Navbar/>
        <Announcement/>
        <Wrapper>
            <ImgContainer>
                <Image src = {product.img}/>
            </ImgContainer>
            <InfoContainer>
                <Title>{product.title}</Title>
                <Desc>{product.desc}</Desc>
                <Price>${product.price}</Price>
                <FilterContainer>
                    <Filter>
                        <FilterTitle>Color: </FilterTitle>
                        {product.color?.map((c) => (
                <FilterColor color={c} key={c} onClick={() => setColor(c)} />
              ))}
                        
                        
                    </Filter>

                    {/*<Filter>
                        <FilterTitle>Size</FilterTitle>
                        <FilterQuantity>
                            <FilterQuantityOption>Single</FilterQuantityOption>
                            <FilterQuantityOption>Half Dozen</FilterQuantityOption>
                            <FilterQuantityOption>Dozen</FilterQuantityOption>
                           
                        </FilterQuantity>
  </Filter>*/}

                </FilterContainer>

                <AddContainer>
                  <AmountContainer>
                    <Remove onClick = {()=> handleQuantity("decrease")}/>
                    <Amount>{quantity}</Amount>
                    <Add onClick = {()=> handleQuantity("increase")}/>
                  </AmountContainer>

                </AddContainer>

                <BuyWishContainer>
                  <Button onClick={handleClick}>ADD TO CART</Button>
                  
                  </BuyWishContainer>
                  <WishlistAdd>Add to Wishlist</WishlistAdd>
                  

            </InfoContainer>
        </Wrapper>
        <Newsletter/>
        <Footer/>
    </Container>
  )
}

export default Product
