import {Search, ShoppingCartOutlined, LocalFlorist} from "@material-ui/icons";
import {Badge} from "@material-ui/core";
import React from 'react';
import styled from 'styled-components';
import {mobile} from "../responsive";

const Container = styled.div`
    height: 60px;
    ${mobile({height: "50px"})}
`;
const Wrapper = styled.div`
    padding: 10px 20px;
    display: flex;
    align-items: center;
    justify-content: space-between;
    ${mobile({padding: "10px 0px"})}
`;
const Left = styled.div`
   flex: 1;
   display: flex;
   align-items: center;

`;
const Language = styled.span`
    font-size: 14px;
    cursor: pointer;
    ${mobile({display: "none"})}
`;
const SearchContainer = styled.div`
    width: 50%;
    border: .5px solid gray;
    border-radius: 15px;
   
    display: flex;
  
    align-items: center;
    margin-left: 25px;
    padding: 5px;
    
`;
const Input = styled.input`
    border: none;
    ${mobile({width: "50px"})}
`

const Center = styled.div`
    flex: 1;
`;
const Logo = styled.h1`
    font-weight: bold;
    text-align: center;
    ${mobile({fontSize: "24px", padding: "0px 5px"})}
`
const Right = styled.div`
    flex: 1;
    display: flex;
    align-items: center;
    justify-content: flex-end;
    ${mobile({flex: 2, justifyContent: "center"})}
`;
const MenuItem = styled.div`
    font-size: 14px;
    cursor: pointer;
    margin-left: 25px;
    ${mobile({fontSize: "12px", marginLeft: "5px", marginRight: "5px"})}
`

const Navbar = () => {
    return (
        <Container>
            <Wrapper>
                <Left>
                <Logo>Mocha.Art</Logo>
                </Left>
                <Center>
                    
                    <Language><LocalFlorist/></Language>
                    <SearchContainer>
                        <Input placeholder="Search"/> 
                        <Search style = {{color: "gray", fontSize:22}}/>
                    </SearchContainer>
                </Center>
                <Right>
                    <MenuItem>Register</MenuItem>
                    <MenuItem>Sign In</MenuItem>
                    <MenuItem>
                    <Badge badgeContent={4} color="secondary">
                        <ShoppingCartOutlined/>
                    </Badge>
                    </MenuItem>
                </Right>
            </Wrapper>
        </Container>
    )
}

export default Navbar
