import styled from 'styled-components';
import {Facebook, Instagram, Twitter, Room, Phone, MailOutline} from "@material-ui/icons";
import { mobile } from '../responsive';
import {
  Link
} from "react-router-dom";

const Container = styled.div`
  display: flex;
  ${mobile({flexDirection: "column"})}
`;
const Left = styled.div`
  flex:1;
  display: flex;
  flex-direction: column;
  padding: 20px 50px;
  
`;
const Logo= styled.h1`

`;
const Description= styled.p`
  margin: 20px 0px;
`;
const SocialsContainer= styled.div`
  display: flex;
`;
const SocialIcon= styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  color: white;
  background-color: #${props=>props.color};
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 20px;
`;
const Center = styled.div`
    flex:1;
    padding: 20px;
    ${mobile({display: "none"})}
`;


const Title= styled.h3`
  margin-bottom: 20px;
  
`
const List = styled.ul`
  margin: 0;
  padding: 0px;
  list-style: none;
  display: flex;
  flex-wrap: wrap;
`
const ListItem = styled.li`
  width: 50%;
  margin-bottom: 10;
`
const Right = styled.div`
    flex:1;
    padding: 20px;
    
   
    ${mobile({backgroundColor: "teal", color: "white"})}
`;

const ContactItem = styled.div`
  margin-bottom: 20px;
  display: flex;
  align-items: center;
`
const linkStyle = {
    
  textDecoration: "none",
  color: 'black'
};

const Footer = () => {
  return (
    <Container>
        <Left>
            <Link to="/" style={linkStyle}>
              <Logo>Mocha.Art</Logo>
            </Link>
            <Description>With over 10 years of experience, Mocha.Art specializes in crepe paper flower art. 
              We offer a range of options from roses to peonies to orchids, etc.</Description>
            
        </Left>
        <Center>
          <Title>Resources & Links</Title>
          <List>
            <ListItem><Link to = '/' style={linkStyle}>Home </Link></ListItem>
            <ListItem>Accessibility</ListItem>
            <ListItem><Link to = '/products/flower' style={linkStyle}>Products </Link></ListItem>
            <ListItem>Privacy Policy</ListItem>
            <ListItem><Link to = '/register' style={linkStyle}>Create Account </Link></ListItem>
            <ListItem>Terms of Service</ListItem>
          </List>
        </Center>
        
        <Right>
          <Title>Contact</Title>
            <ContactItem>
              <Room style={{marginRight: "10px"}}/>IL, US
            </ContactItem>
            <ContactItem>
              <Phone style={{marginRight: "10px"}}/>+1 (123) 456-7890
            </ContactItem>
            <ContactItem>
              <MailOutline style={{marginRight: "10px"}}/>contact@mochaart.dev
            </ContactItem>   
        </Right>
        <Center>
         
          <Description>CONNECT WITH US</Description>
            <SocialsContainer>
                <SocialIcon color = "3b5998">
                    <Facebook/>
                </SocialIcon>
                <SocialIcon color = "E1306C">
                    <Instagram/>
                </SocialIcon>
                <SocialIcon color= "00aced">
                    <Twitter/>
                </SocialIcon>
            </SocialsContainer>
        </Center>
    
    </Container>
       
  );
};

export default Footer;
