import styled from 'styled-components';
import {Facebook, Instagram, Twitter} from "@material-ui/icons";
import { mobile } from '../responsive';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  ${mobile({flexDirection: "column"})}
`;
const Description= styled.p`
  margin: 20px 0px;
 
`;
const SocialsContainer= styled.div`
  display: flex;
  padding: 10px;
`;
const SocialIcon= styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  color: white;
  background-color: #${props=>props.color};
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 20px;
`;
const Center = styled.div`
display: flex;
align-items: center;
justify-content: center;
padding: 10px;
    ${mobile({display: "none"})}
`;




const BottomCenter = styled.div`
display: flex;
align-items: center;
justify-content: center;
padding: 20px;`


const SubFooter = () => {
    return (
      <Container>
          
          
      
          <BottomCenter>
           
           <Description>CONNECT WITH US</Description>
             <SocialsContainer>
                 <SocialIcon color = "3b5998">
                     <Facebook/>
                 </SocialIcon>
                 <SocialIcon color = "E1306C">
                     <Instagram/>
                 </SocialIcon>
                 <SocialIcon color= "00aced">
                     <Twitter/>
                 </SocialIcon>
             </SocialsContainer>
         </BottomCenter>

         <Center>
            <Description> &copy; 2022 Gabriela Brito</Description>
            
          </Center>

      </Container>
         
    );
  };
  
  export default SubFooter;
