import styled from 'styled-components';
import {mobile} from "../responsive";
import {  useState } from "react";
import {useDispatch, useSelector} from "react-redux";
import {login} from "../redux/apiCalls";


const Container = styled.div`
  width: 100vw;
  height: 100vh;
  background: linear-gradient(rgba(255, 255, 255, 0.5),
  rgba(255, 255, 255, 0.5)),
  url("https://images.unsplash.com/photo-1588920592494-8483d3fa89e4?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=687&q=80") 
  center;
  background-size: cover;
  display: flex;
  align-items: center;
  justify-content: center;
`
const Wrapper = styled.div`
  width: 20%;
  padding: 20px;
  background-color: white;
  ${mobile({width: "75%"})}
`
const Title = styled.h1`
  font-size: 24px;
  font-weight: 400;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #985E6D;
`
const Form = styled.form`
  display: flex;
  flex-direction: column;
  display: flex;
  align-items: center;
  justify-content: center;
`

const Input = styled.input`
  flex: 1;
  width: 60%;
  margin: 10px 0px;
  padding: 10px;
`
const Button = styled.button`
  width: 50%;
  border: none;
  
  background-color: #985E6D;
  color: white;
  padding: 10px 20px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  margin: 4px 2px;
  cursor: pointer;
  border-radius: 32px;
  font-size: 20px;
  margin-bottom: 10px;
  &:disabled {
    cursor: not-allowed;
  }
`
const Link = styled.a`
  margin: 5px 0px;
  font-size: 12px;
  text-decoration: underline;
  cursor: pointer;
`
const Error = styled.span`
  color: red;
`


const SignIn = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const dispatch = useDispatch();
  const {isFetching, error} = useSelector((state)=> state.user);

  const handleClick = (e)=>{
    e.preventDefault();
    login(dispatch, {username, password});

  };
  return (
   <Container>
     <Wrapper>
            <Title>SIGN IN</Title>
            <Form>
                
                <Input placeholder="USERNAME" onChange={(e)=> setUsername(e.target.value)}/>
                <Input placeholder="PASSWORD" type = "password" onChange={(e)=> setPassword(e.target.value)}/>
                <Button onClick = {handleClick} disabled={isFetching}>SIGN IN</Button>
                {error && <Error>Wrong Credentials</Error>}
                <Link>RESET PASSWORD</Link>
                <Link to="/register">CREATE NEW ACCOUNT</Link>
            </Form>
        </Wrapper>
   </Container>
  );
};

export default SignIn;
