import styled from 'styled-components'
import {LocalShippingOutlined, VerifiedUserOutlined, CachedOutlined, LocalOffer} from "@material-ui/icons";

import {mobile, tablet, laptop} from "../responsiveDesign";

const BotContainer = styled.div`
width: 99%;
height: 26vh;
display: grid;
background-color: white;
grid-template-columns: auto auto auto auto;
grid-gap: 10px;
padding: 10px;


${mobile({display: "none"})}
${tablet({display: "grid", width: "96%"})}
${laptop({width: "100%"})}
`;
const BotWrapper1 = styled.div`
padding: 20px 0;
    background-color: #192231;
    
    border-style: solid;
  border-color: #494E6B;
    
    
`;
const BotWrapper2 = styled.div`
padding: 20px 0;
background-color: #192231;
    
border-style: solid;
border-color: #494E6B;
    
`;
const BotWrapper3 = styled.div`
padding: 20px 0;
background-color: #192231;
    
border-style: solid;
border-color: #494E6B;
    
`;
const BotWrapper4 = styled.div`
padding: 20px 0;
background-color: #192231;
    
border-style: solid;
border-color: #494E6B;
    
    
    
`;
const Icons = styled.div`
    transform: scale(1.8);
    display: flex;
    align-items: center;
    justify-content: center;
    padding-top: 70px;
    padding-bottom: 30px;
    color: #985E6D;

`
const Title = styled.h3`
    display: flex;
    align-items: center;
    justify-content: center;
    color: #98878F;
    ${tablet({fontSize: "14px"})}
`

const InfoBar = () => {

return (

    <BotContainer>
        <BotWrapper1>
            <Icons>
                <LocalShippingOutlined/>
            </Icons>
            <Title>Free Shipping On Orders Over $49</Title>
        </BotWrapper1>
        <BotWrapper2>
            <Icons>
                <VerifiedUserOutlined/>
            </Icons>
            <Title>Secure Payments Guaranteed!</Title>
        </BotWrapper2>
        <BotWrapper3>
            <Icons>
                <CachedOutlined/>    
            </Icons>
            <Title>Easy Returns and Exchanges</Title>
        </BotWrapper3>
        <BotWrapper4>
            <Icons>
                <LocalOffer/>
            </Icons>
            <Title>Save big with weekly deals!</Title>
        </BotWrapper4>

    </BotContainer>


)
 
};

export default InfoBar
