import styled from 'styled-components';
import {Facebook, Instagram, Twitter, Room, Phone, MailOutline} from "@material-ui/icons";

const Container = styled.div`
  display: flex;
`;
const Left = styled.div`
  flex:1;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;
const Logo= styled.h1`

`;
const Desc= styled.p`
  margin: 20px 0px;
`;
const SocialContainer= styled.div`
  display: flex;
`;
const SocialIcon= styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  color: white;
  background-color: #${props=>props.color};
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 20px;
`;
const Center = styled.div`
    flex:1;
    padding: 20px;
`;


const Title= styled.h3`
  margin-bottom: 20px;
`
const List = styled.ul`
  margin: 0;
  padding: 0;
  list-style: none;
  display: flex;
  flex-wrap: wrap;
`
const ListItem = styled.li`
  width: 50%;
  margin-bottom: 10;
`
const Right = styled.div`
    flex:1;
    padding: 20px;
`;

const ContactItem = styled.div`
  margin-bottom: 20px;
  display: flex;
  align-items: center;
`
const Payment = styled.img`
  width: 50%;
`

const Footer = () => {
  return (
    <Container>
        <Left>
            <Logo>Mocha.Art</Logo>
            <Desc>Small description of what the website represents, which audience it is for and what it is trying to achieve.</Desc>
            <SocialContainer>
                <SocialIcon color = "3B5999">
                    <Facebook/>
                </SocialIcon>
                <SocialIcon color = "E1306C">
                    <Instagram/>
                </SocialIcon>
                <SocialIcon color= "55ACEE">
                    <Twitter/>
                </SocialIcon>
            </SocialContainer>
        </Left>
        <Center>
          <Title>Useful Links</Title>
          <List>
            <ListItem>Home</ListItem>
            <ListItem>Cart</ListItem>
            <ListItem>Roses</ListItem>
            <ListItem>Flowers</ListItem>
            <ListItem>Wishlist</ListItem>
            <ListItem>Terms</ListItem>
          </List>
        </Center>
        <Right>
          <Title>Contact</Title>
          <ContactItem>
            <Room/>1234 Mocha Street Chicago, IL 12345
          </ContactItem>
          <ContactItem>
            <Phone/>+1 (123) 456-7890
          </ContactItem>
          <ContactItem>
            <MailOutline/>contact@mochaart.dev
          </ContactItem>
          <Payment src = "https://prepbootstrap.com/Content/images/shared/misc/creditcardicons.png"/>
        </Right>
    </Container>
  )
}

export default Footer
