import styled from 'styled-components';
import Bulletin from '../components/Bulletin';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import SubFooter from '../components/SubFooter';
import PreFooter from '../components/PreFooter';
import {Add, Remove} from "@material-ui/icons";
import { mobile } from '../responsive';
import { useSelector } from "react-redux";
import {useLocation} from "react-router-dom";
import { useEffect, useState } from "react";
import { publicRequest } from '../requestMethods';

import {
  Link
} from "react-router-dom";
import { removeProduct, incrementQuantity, decrementQuantity } from "../redux/cartRedux";
import {useDispatch} from "react-redux";

const Container = styled.div`

`
const Wrapper = styled.div`
  padding: 20px;
  ${mobile({padding: "10px"})}
`
const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`
const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
`
const TopButton = styled.button`
  padding: 10px 20px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  margin: 4px 2px;
  cursor: pointer;
  border-radius: 32px;
  font-size: 15px;
  border: ${props=>props.type === "filled" && "none"};
  background-color: ${props=>props.type === "filled" ? "black" : "transparent"};
  color: ${props=>props.type === "filled" && "white"};

`
const TopTexts = styled.div`
  ${mobile({display: "none"})}
`
const TopText = styled.span`
 text-decoration: underline;
 cursor: pointer;
 margin: 0px 10px;
`

const Bottom = styled.div`
  display: flex;
  justify-content: space-between;
  ${mobile({flexDirection: "column"})}
`
const Info = styled.div`
  flex: 3;
  
`
const Product = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 5px;
  ${mobile({flexDirection: "column"})}

`
const ProductDetail = styled.div`
  flex: 2;
  display: flex;
 
  
`
const Image = styled.img`
  width: 200px;
`
const Details = styled.div`
  padding: 20px;
  display: flex; 
  flex-direction: column;
  justify-content: space-around;
 
`
const ProductName = styled.span`

`
const ProductId = styled.span`

`
const ProductColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props)=> props.color};
  border-style: solid;
  border-color: #192231;
`
const ProductAmount = styled.span`
  width: 30px;
  height: 30px;
  border-radius: 10px;
  border: 2px solid #192231;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0px 5px;
`
const PriceDetail = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`
const ProductAmtContainer = styled.span`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
  padding: 20px 20px;
  border-color: #192231;
`
const ProductAmt = styled.span`
  font-size: 24px;
  margin: 5px;
 
 
  
  ${mobile({margin: "5px 15px"})}
  
  


`
const ProductPrice = styled.span`
  font-size: 16px;
  font-weight: 400;
  ${mobile({marginBottom: "20px"})}
`
const Hr = styled.div`
  background-color: #eee;
  border: none;
  height: 1px;
`

const Summary = styled.div`
  flex: 1;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: 50vh;
`
const SummaryTitle = styled.h1`
  font-weight: 200;

` 
const SummaryItem = styled.div`
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
  font-weight: ${props=>props.type === "total" && "500"};
  font-size: ${props=>props.type === "total" && "24px"};
` 
const SummaryItemText = styled.span`
  
` 
const SummaryItemPrice = styled.span`
  
` 
const SummarySearchContainer = styled.div`
  display: flex;
  width: 50%;

`
const Input = styled.input`
  width: 100%;
  
  `
const ProductDesc = styled.span``
const SummaryButton = styled.button`
  width: 100%;
  padding: 10px 20px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  margin: 4px 2px;
  cursor: pointer;
  border-radius: 32px;
  font-size: 18px;
  color: white;
  background-color: black;
` 
const SummaryCodeButton=styled.button`
  width: 50%;
  padding: 3px;
  text-align: center;
  display: inline-block;
  margin: 4px 2px;
  cursor: pointer;
  border-radius: 32px;
  color: white;
  background-color: black;
  `
const linkStyle = {
    
  textDecoration: "none",
  color: 'black'
};

const Cart = () => {
  const cart = useSelector((state) => state.cart);
  const [quantity, setQuantity] = useState();
  const location = useLocation();
  const id = location.pathname.split("/")[2];
  const [product, setProduct] = useState({});
  const dispatch = useDispatch();
  

  useEffect(() =>{
    const getProduct = async ()=>{
      try{
        const res = await publicRequest.get("/products/find/" + id)
        setProduct(res.data);
      }catch{}
    };
    getProduct();
  }, [id]);
 
   //function to handle quantity increase and decrease 
   const handleQuantity = (type) =>{
    if(type === "decrease"){
      quantity > 1 && setQuantity(quantity-1);
    }else{
      setQuantity(quantity+1);
    }
   
  };
  return (
    <Container>
        <Navbar/>
        <Bulletin/>
        <Wrapper>
        <Title>MY SHOPPING CART</Title>
            <Top>
                <Link to = '/products/flower' style={linkStyle}>
                <TopButton>CONTINUE SHOPPING</TopButton>
                </Link>
                
                <TopButton type="filled">CHECK OUT NOW</TopButton>
            </Top>
            <Bottom>
                <Info>
                {cart.products.map((product) => (

                    <Product>
                        <ProductDetail>
                       
                        <Image src={product.img}/>
                        <Details>
                          <ProductName><b>Product: </b>{product.title}</ProductName>
                          <ProductDesc><b>Details: </b>{product.desc}</ProductDesc>
                          
                          <ProductId><b>Item Id: </b> {product._id}</ProductId>
                          <ProductPrice> <b>Price: </b>${product.price * product.quantity}.00</ProductPrice>
                          Color:
                          <ProductColor color={product.color}/>
                          
                        </Details>
                        </ProductDetail>
                        <PriceDetail>
                        Quantity:
                          <ProductAmtContainer>
                            
                            <Add onClick = {()=> dispatch(incrementQuantity(product._id))}/>
                            <ProductAmount>{product.quantity}</ProductAmount>
                            <Remove onClick = {()=> dispatch(decrementQuantity(product._id))}/>
                          </ProductAmtContainer>
                          
                        </PriceDetail>
                        <button
                          className='cartItem__removeButton' 
                          onClick={() => dispatch(removeProduct(product._id))}>
                          Remove
                        </button>
                    </Product>
                     ))}
                    <Hr/>
                </Info>
                <Summary>
                  <SummaryTitle>Order Summary</SummaryTitle>
                  <SummaryItem>
                    <SummaryItemText>Subtotal</SummaryItemText>
                    <SummaryItemPrice>$ {cart.total}.00</SummaryItemPrice>
                  </SummaryItem>
                  <SummaryItem>
                    <SummaryItemText>Estimated Shipping</SummaryItemText>
                    <SummaryItemPrice>$5.00</SummaryItemPrice>
                  </SummaryItem>
                  <SummaryItem>
                    <SummaryItemText>Shipping Discount</SummaryItemText>
                    <SummaryItemPrice>-$5.00</SummaryItemPrice>
                  </SummaryItem>
                  <SummaryItem>
                    <SummaryItemText>Add Code:</SummaryItemText>
                    <SummarySearchContainer>
                        <Input placeholder="FLOWERS4U"/> 
                        <SummaryCodeButton>Apply</SummaryCodeButton>
                    </SummarySearchContainer>
                  </SummaryItem>
                  <SummaryItem type="total">
                    <SummaryItemText>Total</SummaryItemText>
                    <SummaryItemPrice>$ {cart.total}.00</SummaryItemPrice>
                  </SummaryItem>
                  <SummaryButton>CHECKOUT</SummaryButton>
                </Summary>
            </Bottom>
        </Wrapper>
        <PreFooter/>
      
      <Footer/>
      
      <SubFooter/>
    </Container>
  )
}

export default Cart
