import styled from 'styled-components'
import {LocalShippingOutlined, VerifiedUserOutlined, CachedOutlined, LocalOffer} from "@material-ui/icons";

import {mobile} from "../responsive";

const BotContainer = styled.div`
width: 100%;
height: 30vh;
display: grid;
background-color: white;
grid-template-columns: auto auto auto auto;
grid-gap: 10px;
padding: 10px;
${mobile({display: "none"})}
`
const BotWrapper1 = styled.div`
padding: 20px 0;
    background-color:#aaa;
    
    
`
const BotWrapper2 = styled.div`
padding: 20px 0;
    background-color:#bbb;
    
`
const BotWrapper3 = styled.div`
padding: 20px 0;
    background-color:#ccc;
    
`
const BotWrapper4 = styled.div`
padding: 20px 0;
    background-color:#ddd;
    
    
    
`
const Icons = styled.div`
    transform: scale(1.8);
    display: flex;
    align-items: center;
    justify-content: center;
    padding-top: 70px;
    padding-bottom: 30px;

`
const Title = styled.h3`
display: flex;
    align-items: center;
    justify-content: center;
  
`

const InfoBar = () => {

return (

    <BotContainer>
        <BotWrapper1>
            <Icons>
                <LocalShippingOutlined/>
            </Icons>
            <Title>Free Shipping on Orders Over $49</Title>
        </BotWrapper1>
        <BotWrapper2>
            <Icons>
                <VerifiedUserOutlined/>
            </Icons>
            <Title>Secure Payments</Title>
        </BotWrapper2>
        <BotWrapper3>
            <Icons>
                <CachedOutlined/>    
            </Icons>
            <Title>Speedy Returns</Title>
        </BotWrapper3>
        <BotWrapper4>
            <Icons>
                <LocalOffer/>
            </Icons>
            <Title>Save big with weekly deals </Title>
        </BotWrapper4>

    </BotContainer>


)
 
};

export default InfoBar
