import styled from 'styled-components';
import Announcement from '../components/Announcement';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';
import {Add, Remove} from "@material-ui/icons";
import { mobile } from '../responsive';
import { useSelector } from "react-redux";

const Container = styled.div`

`
const Wrapper = styled.div`
  padding: 20px;
  ${mobile({padding: "10px"})}
`
const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`
const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
`
const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  cursor: pointer;
  border: ${props=>props.type === "filled" && "none"};
  background-color: ${props=>props.type === "filled" ? "black" : "transparent"};
  color: ${props=>props.type === "filled" && "white"};

`
const TopTexts = styled.div`
  ${mobile({display: "none"})}
`
const TopText = styled.span`
 text-decoration: underline;
 cursor: pointer;
 margin: 0px 10px;
`

const Bottom = styled.div`
  display: flex;
  justify-content: space-between;
  ${mobile({flexDirection: "column"})}
`
const Info = styled.div`
  flex: 3;
  
`
const Product = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 5px;
  ${mobile({flexDirection: "column"})}

`
const ProductDetail = styled.div`
  flex: 2;
  display: flex;
  
`
const Image = styled.img`
  width: 200px;
`
const Details = styled.div`
  padding: 20px;
  display: flex; 
  flex-direction: column;
  justify-content: space-around;
  background-color: teal;
`
const ProductName = styled.span`

`
const ProductId = styled.span`

`
const ProductColor = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props)=> props.color};
 
`
const ProductAmount = styled.span`

`
const PriceDetail = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`
const ProductAmtContainer = styled.span`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`
const ProductAmt = styled.span`
  font-size: 24px;
  margin: 5px;
  ${mobile({margin: "5px 15px"})}

`
const ProductPrice = styled.span`
  font-size: 30px;
  font-weight: 200;
  ${mobile({marginBottom: "20px"})}
`
const Hr = styled.div`
  background-color: #eee;
  border: none;
  height: 1px;
`

const Summary = styled.div`
  flex: 1;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: 50vh;
`
const SummaryTitle = styled.h1`
  font-weight: 200;

` 
const SummaryItem = styled.div`
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
  font-weight: ${props=>props.type === "total" && "500"};
  font-size: ${props=>props.type === "total" && "24px"};
` 
const SummaryItemText = styled.span`
  
` 
const SummaryItemPrice = styled.span`
  
` 
const SummaryButton = styled.button`
  width: 100%;
  padding: 10px;
  background-color: black;
  color: white;
  font-weight: 600;
` 

const Cart = () => {
  const cart = useSelector((state) => state.cart);
  return (
    <Container>
        <Navbar/>
        <Announcement/>
        <Wrapper>
            <Title>YOUR CART</Title>
            <Top>

                <TopButton>CONTINUE SHOPPING</TopButton>
                <TopTexts>
                    <TopText>Shopping Bag(2)</TopText>
                    <TopText>Your Wishlist(0)</TopText>
                </TopTexts>
                <TopButton type="filled">CHECK OUT NOW</TopButton>
            </Top>
            <Bottom>
                <Info>
                {cart.products.map((product) => (
                    <Product>
                        <ProductDetail>
                        <Image src={product.img}/>
                        <Details>
                          <ProductName><b>Product:</b>{product.title}</ProductName>
                          <ProductId><b>ID:</b> {product._id}</ProductId>
                          <ProductColor color={product.color}/>
                          <ProductAmount><b>Description:</b>{product.desc}</ProductAmount>
                        </Details>
                        </ProductDetail>
                        <PriceDetail>
                          <ProductAmtContainer>
                            <Add/>
                            <ProductAmt>2</ProductAmt>
                            <Remove/>
                          </ProductAmtContainer>
                          <ProductPrice>$20</ProductPrice>
                        </PriceDetail>
                    </Product>
                     ))}
                    <Hr/>
                </Info>
                <Summary>
                  <SummaryTitle>Order Summary</SummaryTitle>
                  <SummaryItem>
                    <SummaryItemText>Subtotal</SummaryItemText>
                    <SummaryItemPrice>$50.00</SummaryItemPrice>
                  </SummaryItem>
                  <SummaryItem>
                    <SummaryItemText>Estimated Shipping</SummaryItemText>
                    <SummaryItemPrice>$5.00</SummaryItemPrice>
                  </SummaryItem>
                  <SummaryItem>
                    <SummaryItemText>Shipping Discount</SummaryItemText>
                    <SummaryItemPrice>-$5.00</SummaryItemPrice>
                  </SummaryItem>
                  <SummaryItem type="total">
                    <SummaryItemText>Total</SummaryItemText>
                    <SummaryItemPrice>$50.00</SummaryItemPrice>
                  </SummaryItem>
                  <SummaryButton>CHECKOUT</SummaryButton>
                </Summary>
            </Bottom>
        </Wrapper>
        <Footer/>
    </Container>
  )
}

export default Cart
